import React from "react";

const MatchupCard = ({ matchup }) => {
	let mm1 = matchup.team_1.money_multiplier;
	let mm2 = matchup.team_2.money_multiplier;
	if (mm1 == null) {
		mm1 = 0;
	}
	if (mm2 == null) {
		mm2 = 0;
	}
	let wp1 = matchup.team_1.win_probability;
	let wp2 = matchup.team_2.win_probability;
	if (wp1 == null) {
		wp1 = 0;
	}
	if (wp2 == null) {
		wp2 = 0;
	}
	let betAmount = 25;
	let profit1 = betAmount * mm1 - betAmount;
	let profit2 = betAmount * mm2 - betAmount;
	let ev1 = (profit1 * wp1 + -betAmount * (1 - wp1)).toFixed(2);
	let ev2 = (profit2 * wp2 + -betAmount * (1 - wp2)).toFixed(2);

	return (
		<div className="matchup-card">
			<div className="matchup-card-header">
				<p>
					{matchup.time} {matchup.date}
				</p>
			</div>
			<div className="teams">
				<div className="team">
					<img
						className="img-fluid"
						src="../img/Atlanta.png"
						alt="Team Logo"
					></img>
					<p>{matchup.team_1.full_name}</p>
				</div>
				<p>@</p>
				<div className="team">
					<img
						className="img-fluid"
						src="../img/Boston.png"
						alt="Team Logo"
					></img>
					<p>{matchup.team_2.full_name}</p>
				</div>
			</div>
			<div className="bet-previews">
				<div className="bet-preview">
					{/* <p>
							Profit <span>${profit1}</span> on {matchup.team_1.short_name} win
							with {wp1}
							{"% "}
							chance.
						</p> */}
					<p>
						${betAmount} bet on {matchup.team_1.short_name} has expected value
						of ${ev1}
					</p>
				</div>

				<div className="bet-preview">
					{/* <p>
							Profit <span>${profit2}</span> on {matchup.team_2.short_name} win
							with {wp2}
							{"% "}
							chance.
						</p> */}

					<p>
						${betAmount} bet on {matchup.team_2.short_name} has expected value
						of ${ev2}
					</p>
				</div>
			</div>
			<button>Place a Bet</button>
		</div>
	);
};

export default MatchupCard;
