import React from "react";

const MatchupCard = ({ matchup }) => {
	let mm1 = matchup.team_1.money_multiplier;
	let mm2 = matchup.team_2.money_multiplier;
	if (mm1 == null) {
		mm1 = 0;
	}
	if (mm2 == null) {
		mm2 = 0;
	}
	let wp1 = (matchup.team_1.win_probability * 100).toFixed(0);
	let wp2 = (matchup.team_2.win_probability * 100).toFixed(0);
	if (wp1 == null) {
		wp1 = 0;
	}
	if (wp2 == null) {
		wp2 = 0;
	}
	let betAmount = 25;
	let profit1 = (betAmount * mm1 - betAmount).toFixed(2);
	let profit2 = (betAmount * mm2 - betAmount).toFixed(2);

	return (
		<div className="col">
			<div className="matchup-card">
				<div className="matchup-card-header">
					<p>
						{matchup.time} {matchup.date}
					</p>
				</div>
				<div className="teams">
					<div className="team">
						<img
							className="img-fluid"
							src="../img/Atlanta.png"
							alt="Team Logo"
						></img>
						<p>{matchup.team_1.full_name}</p>
					</div>
					<p>@</p>
					<div className="team">
						<img
							className="img-fluid"
							src="../img/Boston.png"
							alt="Team Logo"
						></img>
						<p>{matchup.team_2.full_name}</p>
					</div>
				</div>
				<div className="bet-previews">
					<div className="bet-preview">
						<p>
							Profit <span>${profit1}</span> on {matchup.team_1.short_name} win
							with {wp1}
							{"% "}
							chance.
						</p>
					</div>

					<div className="bet-preview">
						<p>
							Profit <span>${profit2}</span> on {matchup.team_2.short_name} win
							with {wp2}
							{"% "}
							chance.
						</p>
					</div>
				</div>
				<button>Place a Bet</button>
			</div>
		</div>
	);
};

export default MatchupCard;
