import "./App.css";
import Navbar from "./components/Navbar";
import Showcase from "./components/Showcase";
import MatchupGrid from "./components/MatchupGrid";
import Sidebar from "./components/Sidebar";
import { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
	const available_sports = [
		"nba",
		"nhl",
		// "ufc",
		// "ncaab",
		// "ncaaf",
		"nfl",
		// "boxing",
	];

	const initialFetchAllMatchups = async () => {
		// Each element of promises array is an HTTP request.
		// Create a request for each sport.
		const promises = [];
		available_sports.forEach((sport_to_fetch) => {
			console.log(sport_to_fetch);
			promises.push(axios(`http://127.0.0.1:5000/api/odds/${sport_to_fetch}`));
		});

		// Await resolve of requests and create an array to combine
		// the JSON objects from each request.  Add each JSON object
		// to session storage as well.
		const res = await Promise.all(promises);
		const all_matchups = [];
		let match_id = 0;
		res.forEach((sport) => {
			sport.data.forEach((match) => {
				all_matchups.push(match);
				sessionStorage.setItem(match_id, JSON.stringify(match));
				match_id++;
			});
		});
		return all_matchups;
		// Set the state to this new array of all the JSON objects.
		// setMatchups(all_matchups);
		// setIsLoading(false);
	};

	// Right now this function runs on every page reload.
	// initialFetchAllMatchups();

	const [matchups, setMatchups] = useState(() => {
		return initialFetchAllMatchups();
	});
	const [isLoading, setIsLoading] = useState(true);

	useEffect(() => {}, []);

	return (
		<div className="App">
			<Navbar></Navbar>
			<Showcase></Showcase>
			<section id="main-content">
				<div className="container">
					<div className="row">
						<div id="sidebar" className="col-3">
							<Sidebar></Sidebar>
						</div>
						<div id="games" className="col-9">
							<div className="email d-flex flex-row justify-content-center align-items-center">
								<form>
									<label for="email">
										Sign up for daily emails of the day's best bets.
									</label>
									<input type="email" id="email" value="Email"></input>

									<button type="submit" class="btn btn-primary mb-2">
										Email Me
									</button>
								</form>
							</div>
							<div className="row justify-content-between">
								<div className="col-3">
									<select
										class="form-select"
										aria-label="Default select example"
									>
										<option selected>Smartest bets first</option>
										<option value="1">Biggest underdogs first</option>
										<option value="2">Biggest favorites first</option>
									</select>
								</div>
								<div className="col-3">
									<p>Showing 1-20 out of {matchups.length} games</p>
								</div>
							</div>

							<MatchupGrid matchups={matchups}></MatchupGrid>
						</div>
					</div>
				</div>
			</section>
		</div>
	);
};

export default App;
